"use client";

import { useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { authService } from '@/services/auth';
import { Loader2, ArrowRight } from 'lucide-react';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const router = useRouter();

    async function handleSubmit(e: React.FormEvent) {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            await authService.login(email, password);
            router.push('/area-do-cliente');
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="min-h-screen bg-[#050505] text-[#ededed] flex">
            {/* Esquerda: Visual / Branding */}
            <div className="hidden lg:flex flex-col justify-between w-1/2 p-12 border-r border-white/10 relative overflow-hidden">
                <div className="absolute inset-0 bg-[radial-gradient(circle_at_left,_var(--tw-gradient-stops))] from-purple-900/20 via-transparent to-transparent opacity-50" />

                <div className="z-10">
                    <Link href="/" className="text-2xl font-black uppercase tracking-tighter hover:text-[#00F0FF] transition-colors">
                        Hoffby
                    </Link>
                </div>

                <div className="z-10 max-w-lg">
                    <h1 className="text-6xl font-black uppercase leading-[0.9] mb-6">
                        Acesse o <span className="text-transparent bg-clip-text bg-gradient-to-r from-[#00F0FF] to-[#CCFF00]">Futuro</span>
                    </h1>
                    <p className="text-lg text-white/60 font-medium">
                        Gerencie suas ferramentas, acompanhe seus projetos e vote nas próximas features.
                        Sua área exclusiva de produtividade.
                    </p>
                </div>

                <div className="z-10 text-xs font-mono text-white/40 uppercase tracking-widest">
                    © {new Date().getFullYear()} Hoffby Tecnologia Ltda. System beta
                </div>
            </div>

            {/* Direita: Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center p-8 relative">
                <div className="w-full max-w-md">
                    <div className="mb-10 lg:hidden">
                        <Link href="/" className="text-2xl font-black uppercase tracking-tighter">Hoffby</Link>
                    </div>

                    <div className="mb-8">
                        <h2 className="text-3xl font-black uppercase mb-2">Login</h2>
                        <p className="text-white/50">Entre com suas credenciais para continuar.</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div>
                            <label className="block text-xs font-bold uppercase tracking-widest mb-2 text-[#00F0FF]">Email</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="brutal-input bg-black border border-white/20 p-4 w-full focus:border-[#00F0FF] focus:outline-none transition-colors"
                                placeholder="seu@email.com"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-xs font-bold uppercase tracking-widest mb-2 text-[#B026FF]">Senha</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="brutal-input bg-black border border-white/20 p-4 w-full focus:border-[#B026FF] focus:outline-none transition-colors"
                                placeholder="••••••••"
                                required
                            />
                        </div>

                        {error && (
                            <div className="p-4 border border-red-500/50 bg-red-500/10 text-red-400 text-sm font-bold">
                                ❌ {error}
                            </div>
                        )}

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-white text-black font-black uppercase tracking-widest py-4 hover:bg-[#00F0FF] hover:translate-x-1 hover:translate-y-1 transition-all disabled:opacity-50 disabled:hover:translate-x-0 disabled:hover:translate-y-0 disabled:hover:bg-white flex items-center justify-center gap-2 group"
                        >
                            {loading ? <Loader2 className="animate-spin" /> : (
                                <>
                                    Entrar <ArrowRight size={18} className="group-hover:translate-x-1 transition-transform" />
                                </>
                            )}
                        </button>

                        <div className="text-center mt-8">
                            <p className="text-sm text-white/40">
                                Não tem uma conta?{' '}
                                <Link href="/register" className="text-white hover:text-[#00F0FF] font-bold underline decoration-2 underline-offset-4 transition-colors">
                                    Criar conta
                                </Link>
                            </p>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { authService } from '@/services/auth';
import { Loader2, ArrowRight } from 'lucide-react';

export default function RegisterPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState(false);
    const router = useRouter();

    async function handleSubmit(e: React.FormEvent) {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            await authService.register(email, password);
            setSuccess(true);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }

    if (success) {
        return (
            <div className="min-h-screen bg-[#050505] text-[#ededed] flex items-center justify-center p-6">
                <div className="max-w-md w-full border border-[#00F0FF] bg-[#00F0FF]/5 p-12 text-center">
                    <h2 className="text-4xl font-black uppercase text-[#00F0FF] mb-4">Verifique seu Email</h2>
                    <p className="text-white/60 mb-8 leading-relaxed">
                        Enviamos um link de confirmação para <strong>{email}</strong>.
                        Por favor, confirme sua conta para acessar a plataforma.
                    </p>
                    <Link href="/login" className="inline-block bg-[#00F0FF] text-black font-black uppercase tracking-widest py-3 px-8 hover:bg-white transition-colors">
                        Voltar para Login
                    </Link>
                </div>
            </div>
        )
    }

    return (
        <div className="min-h-screen bg-[#050505] text-[#ededed] flex">
            {/* Esquerda: Visual / Branding */}
            <div className="hidden lg:flex flex-col justify-between w-1/2 p-12 border-r border-white/10 relative overflow-hidden">
                <div className="absolute inset-0 bg-[radial-gradient(circle_at_left,_var(--tw-gradient-stops))] from-green-900/20 via-transparent to-transparent opacity-50" />

                <div className="z-10">
                    <Link href="/" className="text-2xl font-black uppercase tracking-tighter hover:text-[#CCFF00] transition-colors">
                        Hoffby
                    </Link>
                </div>

                <div className="z-10 max-w-lg">
                    <h1 className="text-6xl font-black uppercase leading-[0.9] mb-6">
                        Crie sua <span className="text-transparent bg-clip-text bg-gradient-to-r from-[#CCFF00] to-[#00F0FF]">Conta</span>
                    </h1>
                    <p className="text-lg text-white/60 font-medium">
                        Junte-se a milhares de desenvolvedores que usam o Hoffby para acelerar seu workflow.
                        É grátis e sempre evoluindo.
                    </p>
                </div>

                <div className="z-10 text-xs font-mono text-white/40 uppercase tracking-widest">
                    © {new Date().getFullYear()} Hoffby Tecnologia Ltda. System beta
                </div>
            </div>

            {/* Direita: Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center p-8 relative">
                <div className="w-full max-w-md">
                    <div className="mb-10 lg:hidden">
                        <Link href="/" className="text-2xl font-black uppercase tracking-tighter">Hoffby</Link>
                    </div>

                    <div className="mb-8">
                        <h2 className="text-3xl font-black uppercase mb-2">Cadastro</h2>
                        <p className="text-white/50">Preencha os dados abaixo para criar sua conta.</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div>
                            <label className="block text-xs font-bold uppercase tracking-widest mb-2 text-[#CCFF00]">Email</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="brutal-input bg-black border border-white/20 p-4 w-full focus:border-[#CCFF00] focus:outline-none transition-colors"
                                placeholder="seu@email.com"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-xs font-bold uppercase tracking-widest mb-2 text-[#B026FF]">Senha</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="brutal-input bg-black border border-white/20 p-4 w-full focus:border-[#B026FF] focus:outline-none transition-colors"
                                placeholder="Crie uma senha forte"
                                required
                            />
                        </div>

                        {error && (
                            <div className="p-4 border border-red-500/50 bg-red-500/10 text-red-400 text-sm font-bold">
                                ❌ {error}
                            </div>
                        )}

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-white text-black font-black uppercase tracking-widest py-4 hover:bg-[#CCFF00] hover:translate-x-1 hover:translate-y-1 transition-all disabled:opacity-50 disabled:hover:translate-x-0 disabled:hover:translate-y-0 disabled:hover:bg-white flex items-center justify-center gap-2 group"
                        >
                            {loading ? <Loader2 className="animate-spin" /> : (
                                <>
                                    Criar Conta <ArrowRight size={18} className="group-hover:translate-x-1 transition-transform" />
                                </>
                            )}
                        </button>

                        <div className="text-center mt-8">
                            <p className="text-sm text-white/40">
                                Já tem uma conta?{' '}
                                <Link href="/login" className="text-white hover:text-[#CCFF00] font-bold underline decoration-2 underline-offset-4 transition-colors">
                                    Fazer Login
                                </Link>
                            </p>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

import { createClient } from '@/lib/supabase-server'
import { NextResponse } from 'next/server'

export async function POST(request: Request) {
    const requestUrl = new URL(request.url)
    const formData = await request.json()
    const email = formData.email
    const password = formData.password
    const supabase = await createClient()

    const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
    })

    if (error) {
        return NextResponse.json({ error: error.message }, { status: 401 })
    }

    return NextResponse.json({ success: true }, { status: 200 })
}

import { createClient } from '@/lib/supabase-server'
import { NextResponse } from 'next/server'

export async function POST(request: Request) {
    const supabase = await createClient()
    await supabase.auth.signOut()
    return NextResponse.json({ success: true }, { status: 200 })
}

import { createClient } from '@/lib/supabase-server'
import { NextResponse } from 'next/server'

export async function GET(request: Request) {
    const supabase = await createClient()

    const { data: { user }, error } = await supabase.auth.getUser()

    if (error || !user) {
        return NextResponse.json({ user: null }, { status: 401 })
    }

    return NextResponse.json({ user }, { status: 200 })
}

import { createClient } from '@supabase/supabase-js';
import { NextResponse } from 'next/server';

export async function POST(request: Request) {
    try {
        const body = await request.json();
        const { personal, resumeData } = body;

        if (!personal || !personal.email) {
            return NextResponse.json({ error: 'Missing personal information' }, { status: 400 });
        }

        // Use Service Role Key to bypass RLS
        const supabaseUrl = process.env.SUPABASE_URL;
        const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

        if (!supabaseUrl || !supabaseServiceKey) {
            const missing = [];
            if (!supabaseUrl) missing.push('SUPABASE_URL');
            if (!supabaseServiceKey) missing.push('SUPABASE_SERVICE_ROLE_KEY');
            console.error(`Missing Supabase credentials: ${missing.join(', ')}`);
            return NextResponse.json({ error: `Server configuration error: Missing ${missing.join(', ')}` }, { status: 500 });
        }

        const supabase = createClient(supabaseUrl, supabaseServiceKey, {
            auth: {
                autoRefreshToken: false,
                persistSession: false
            }
        });

        const { data, error } = await supabase
            .from('boost_leads')
            .upsert({
                email: personal.email,
                full_name: personal.fullName,
                phone: personal.phone,
                resume_data: resumeData,
                status: 'captured', // Initial status before payment
                updated_at: new Date().toISOString()
            }, { onConflict: 'email' })
            .select()
            .single();

        if (error) {
            console.error('Supabase Error:', error);
            return NextResponse.json({ error: 'Failed to save lead', details: error.message }, { status: 500 });
        }

        return NextResponse.json({ success: true, leadId: data.id });

    } catch (error: any) {
        console.error('API Error:', error);
        return NextResponse.json({ error: error.message || 'Internal Error' }, { status: 500 });
    }
}

import { createClient } from '@supabase/supabase-js';
import { NextResponse } from 'next/server';
import { loadEnvConfig } from '@next/env';

// Force load env vars from project root
loadEnvConfig(process.cwd());

const ASAAS_API_URL = process.env.ASAAS_API_URL || 'https://www.asaas.com/api/v3';

export async function POST(request: Request) {
    try {
        // Read directly from process.env to avoid module-time evaluation issues
        const apiKey = process.env.ASAAS_API_KEY;

        if (!apiKey) {
            console.error('DEBUG: ASAAS_API_KEY is missing/empty.');
            return NextResponse.json({ error: 'Server configuration error: Missing ASAAS_API_KEY' }, { status: 500 });
        }
        // console.log('DEBUG: ASAAS_API_KEY Loaded. Length:', apiKey.length);

        const body = await request.json();
        // Allow CPF to be passed in personal object
        const { personal, resumeData } = body;
        const cpfCnpj = personal.cpf || personal.cpfCnpj;

        if (!personal || !personal.email || !personal.fullName) {
            return NextResponse.json({ error: 'Missing personal information' }, { status: 400 });
        }

        // Use Service Role Key to bypass RLS
        const supabaseUrl = process.env.SUPABASE_URL;
        const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

        if (!supabaseUrl || !supabaseServiceKey) {
            const missing = [];
            if (!supabaseUrl) missing.push('SUPABASE_URL');
            if (!supabaseServiceKey) missing.push('SUPABASE_SERVICE_ROLE_KEY');
            console.error(`Missing Supabase credentials: ${missing.join(', ')}`);
            return NextResponse.json({ error: `Server configuration error: Missing ${missing.join(', ')}` }, { status: 500 });
        }

        const supabase = createClient(supabaseUrl, supabaseServiceKey, {
            auth: {
                autoRefreshToken: false,
                persistSession: false
            }
        });

        // 1. Save Lead/Resume to Supabase
        const { data: leadData, error: dbError } = await supabase
            .from('boost_leads')
            .upsert({
                email: personal.email,
                full_name: personal.fullName,
                phone: personal.phone,
                resume_data: resumeData,
                status: 'pending_payment',
                updated_at: new Date().toISOString()
            }, { onConflict: 'email' })
            .select()
            .single();

        if (dbError) {
            console.error('Supabase Error:', dbError);
            // Optionally continue logic or throw
        }

        // 2. Create/Get Customer in Asaas
        let customerId = '';

        const customerSearch = await fetch(`${ASAAS_API_URL}/customers?email=${personal.email}`, {
            headers: { 'access_token': apiKey }
        });

        const searchResult = await customerSearch.json();

        if (searchResult.data && searchResult.data.length > 0) {
            customerId = searchResult.data[0].id;
        } else {
            // Create Customer
            const { personal, resumeData } = body;

            // Normalize CPF/CNPJ if present
            const cpfCnpj = personal.cpf || personal.cpfCnpj;

            if (!personal || !personal.email || !personal.fullName) {
                return NextResponse.json({ error: 'Missing personal information' }, { status: 400 });
            }

            // ... (Supabase logic unchanged) ... (omitted for brevity in replacement if possible, but replace_file_content needs contiguous block)
            // Actually, to avoid breaking file structure, let's insert the CPF logic where we construct the Asaas Payload.

            // ... (lines 27-66 existing logic) ...

            // 2. Create/Get Customer in Asaas
            // ...

            // Update Customer Payload logic:
            const createCustomer = await fetch(`${ASAAS_API_URL}/customers`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'access_token': apiKey
                },
                body: JSON.stringify({
                    name: personal.fullName,
                    email: personal.email,
                    mobilePhone: personal.phone,
                    cpfCnpj: cpfCnpj, // Sending CPF
                    notificationDisabled: false,
                })
            });
            const customerData = await createCustomer.json();
            if (customerData.errors) {
                console.error('Asaas Create Customer Error:', JSON.stringify(customerData.errors));
                return NextResponse.json({
                    error: 'Error creating payment customer',
                    details: customerData.errors[0]?.description || 'Unknown Asaas Error'
                }, { status: 400 });
            }
            customerId = customerData.id;
        }

        // 3. Create Subscription (Annual Plan)
        const createSubscription = await fetch(`${ASAAS_API_URL}/subscriptions`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'access_token': apiKey
            },
            body: JSON.stringify({
                customer: customerId,
                billingType: 'PIX', // Default to PIX or CREDIT_CARD
                value: 59.90,
                nextDueDate: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString().split('T')[0], // Tomorrow
                cycle: 'YEARLY',
                description: 'Assinatura Anual - Hoffby Boost AI',
                endDate: null
            })
        });

        const subscriptionData = await createSubscription.json();

        if (subscriptionData.errors) {
            console.error('Asaas Subscription Error:', JSON.stringify(subscriptionData.errors));
            return NextResponse.json({
                error: 'Error creating subscription',
                details: subscriptionData.errors[0]?.description || 'Unknown Asaas Error'
            }, { status: 400 });
        }

        // 3.5 Configure Automatic Invoice (NFS-e)
        try {
            // Get Municipal Service
            const servicesReq = await fetch(`${ASAAS_API_URL}/invoices/municipalServices`, {
                headers: { 'access_token': apiKey }
            });
            const servicesData = await servicesReq.json();

            if (servicesData.data && servicesData.data.length > 0) {
                const serviceId = servicesData.data[0].id;

                await fetch(`${ASAAS_API_URL}/subscriptions/${subscriptionData.id}/invoiceSettings`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'access_token': apiKey
                    },
                    body: JSON.stringify({
                        municipalServiceId: serviceId,
                        effectiveDatePeriod: 'ON_PAYMENT_CONFIRMATION', // Issue when paid
                        receivedOnly: true,
                        daysBeforeDueDate: 0,
                        observations: 'Emissão automática Hoffby Boost AI'
                    })
                });
            }
        } catch (invoiceError) {
            console.error('Error configuring invoice settings:', invoiceError);
            // Non-blocking error, continue
        }

        // 4. Update Lead with Subscription ID 
        if (leadData && leadData.id) {
            await supabase.from('boost_leads').update({
                asaas_customer_id: customerId,
                asaas_subscription_id: subscriptionData.id
            }).eq('id', leadData.id);
        }

        // 5. Return Payment URL
        const paymentsReq = await fetch(`${ASAAS_API_URL}/subscriptions/${subscriptionData.id}/payments`, {
            headers: { 'access_token': apiKey }
        });
        const paymentsData = await paymentsReq.json();
        const firstPaymentUrl = paymentsData.data?.[0]?.invoiceUrl || paymentsData.data?.[0]?.bankSlipUrl;

        if (!firstPaymentUrl) {
            return NextResponse.json({ success: true, message: 'Subscription created', subscriptionId: subscriptionData.id });
        }

        return NextResponse.json({ success: true, paymentUrl: firstPaymentUrl });

    } catch (error: any) {
        console.error('API Error:', error);
        return NextResponse.json({ error: error.message || 'Internal Error' }, { status: 500 });
    }
}

import { google } from 'googleapis';
import { NextResponse } from 'next/server';

export async function POST(req: Request) {
  try {
    const body = await req.json();
    const { name, email, message } = body;

    // --- DIAGNÓSTICO DE SEGURANÇA ---
    console.log("--- INICIANDO DEBUG ---");
    console.log("1. Robô tentando acessar:", process.env.GOOGLE_CLIENT_EMAIL);
    console.log("2. ID da Planilha alvo:", process.env.GOOGLE_SHEET_ID);
    // -------------------------------

    const auth = new google.auth.GoogleAuth({
      credentials: {
        client_email: process.env.GOOGLE_CLIENT_EMAIL,
        private_key: process.env.GOOGLE_PRIVATE_KEY?.replace(/\\n/g, '\n'),
      },
      scopes: [
        'https://www.googleapis.com/auth/spreadsheets',
      ],
    });

    const sheets = google.sheets({ auth, version: 'v4' });

    // Tenta apenas ler o título da planilha primeiro para testar permissão
    try {
        await sheets.spreadsheets.get({
            spreadsheetId: process.env.GOOGLE_SHEET_ID,
        });
        console.log("3. Conexão com a planilha: SUCESSO! (Permissão OK)");
    } catch (readError) {
        console.error("3. Conexão com a planilha: FALHOU. O robô foi barrado.");
        throw readError; // Relança o erro para cair no catch lá de baixo
    }

    const date = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });

    await sheets.spreadsheets.values.append({
      spreadsheetId: process.env.GOOGLE_SHEET_ID,
      range: 'A1',
      valueInputOption: 'USER_ENTERED',
      requestBody: {
        values: [[date, name, email, message]],
      },
    });

    return NextResponse.json({ success: true });

  } catch (error: any) {
    console.error("❌ ERRO FINAL:", error.message);
    return NextResponse.json({ success: false, error: error.message }, { status: 500 });
  }
}

