import { NextResponse } from 'next/server';
import { createCustomer, createPixCharge } from '../../services/asaas';
import { createClient } from '@/lib/supabase-server';

export async function POST(req: Request) {
    try {
        const body = await req.json();
        const { name, email, cpf, type, coupon } = body;

        // Basic validation
        if (!name || !email || !cpf) {
            return NextResponse.json(
                { error: 'Dados incompletos (Nome, Email ou CPF)' },
                { status: 400 }
            );
        }

        // Clean CPF
        const cpfClean = cpf.replace(/\D/g, '');

        if (cpfClean.length !== 11) {
            return NextResponse.json(
                { error: 'CPF inválido' },
                { status: 400 }
            );
        }

        let finalValue = 5.00;

        // Validate Coupon if present
        if (coupon) {
            const supabase = await createClient();
            const { data: couponData, error: couponError } = await supabase
                .from('coupons')
                .select('*')
                .eq('code', coupon.toUpperCase().trim())
                .eq('active', true)
                .single();

            if (!couponError && couponData) {
                // Check limits
                if (couponData.max_uses === null || couponData.current_uses < couponData.max_uses) {
                    const discount = couponData.discount_percent || 0;
                    finalValue = 5.00 * (1 - discount / 100);

                    // Ensure we don't charge 0 or negative via this route
                    // (Zero case should be handled by frontend via register-usage, but just in case)
                    if (finalValue < 0.01) finalValue = 0.01; // Minimum charge if not fully free
                }
            }
        }

        // 1. Get or Create Customer
        const customerId = await createCustomer({
            name,
            email,
            cpfCnpj: cpfClean
        });

        // 2. Create Charge
        const result = await createPixCharge(customerId, finalValue);

        return NextResponse.json(result);

    } catch (error: any) {
        console.error('Payment Create API Error:', error);
        return NextResponse.json(
            { error: error.message || 'Erro interno ao criar cobrança' },
            { status: 500 }
        );
    }
}

import { NextResponse } from 'next/server';
import { getPaymentStatus, createInvoice, checkInvoiceExists } from '../../services/asaas';

export async function GET(req: Request) {
    try {
        const { searchParams } = new URL(req.url);
        const paymentId = searchParams.get('id');

        if (!paymentId) {
            return NextResponse.json(
                { error: 'ID do pagamento não fornecido' },
                { status: 400 }
            );
        }

        const result = await getPaymentStatus(paymentId);

        // If payment confirmed, try to create invoice
        if (result.confirmed) {
            // Check if invoice already exists to avoid duplicates
            const hasInvoice = await checkInvoiceExists(paymentId);

            if (!hasInvoice) {
                await createInvoice({
                    paymentId: result.id,
                    customerId: result.customerId,
                    value: result.value,
                    description: 'Download Currículo - Hoffby'
                });
            }
        }

        return NextResponse.json(result);

    } catch (error: any) {
        console.error('Payment Status API Error:', error);
        return NextResponse.json(
            { error: error.message || 'Erro ao verificar status' },
            { status: 500 }
        );
    }
}

import React from 'react';
import { NextResponse } from 'next/server';
import { renderToStream } from '@react-pdf/renderer';
// @ts-ignore
import { ResumePDF } from '@/components/tools/resume-generator/ResumePDF';
import { getPaymentStatus } from '@/app/api/services/asaas';
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase Admin Client (Service Role) to bypass RLS/Policies for this security check
// If SERVICE_ROLE_KEY is not set, it falls back to ANON_KEY (which might work if RLS allows or if table is public)
const supabaseAdmin = createClient(
    process.env.SUPABASE_URL || '',
    process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.SUPABASE_ANON_KEY || ''
);

export async function POST(req: Request) {
    try {
        const body = await req.json();

        // console.log(body)

        const { resumeData, template, paymentId } = body;

        console.log('[API] Resume Download Request. PaymentId:', paymentId);

        if (!resumeData) {
            return NextResponse.json(
                { error: 'Dados do currículo não fornecidos' },
                { status: 400 }
            );
        }

        let isWatermarked = true;

        if (paymentId) {
            try {
                // 1. Security Check: Download Limit (Supabase)
                // Check if this paymentId has already downloaded
                const { data: existingRecord, error: fetchError } = await supabaseAdmin
                    .from('payment_downloads')
                    .select('*')
                    .eq('payment_id', paymentId)
                    .single();

                if (fetchError && fetchError.code !== 'PGRST116') { // PGRST116 = JSON object requested, multiple (or no) results returned (Single result not found)
                    console.error('[Supabase] Error fetching payment record:', fetchError);
                    // Decide fail-open or fail-closed? Fail-closed for security.
                    // But if table doesn't exist yet (user didn't run migration), this blocks downloads.
                    // Warn and proceed? User explicitly asked for security.
                    // We will proceed securely: check payment status.
                }

                if (existingRecord) {
                    console.log('[Supabase] Existing record found:', existingRecord);
                    if (existingRecord.download_count >= existingRecord.max_downloads) {
                        console.warn(`[Security] Download limit exceeded for ${paymentId}. Count: ${existingRecord.download_count}/${existingRecord.max_downloads}`);
                        return NextResponse.json(
                            { error: 'Limite de downloads excedido para este pagamento. (Máx: 1)' },
                            { status: 403 }
                        );
                    }
                }

                // 2. Verify Payment (Asaas) OR Free Usage (UUID)
                const isAsaasPayment = paymentId.startsWith('pay_');

                if (isAsaasPayment) {
                    const paymentStatus = await getPaymentStatus(paymentId);
                    console.log(`[ResumeDownload] Payment ID: ${paymentId}, Status: ${JSON.stringify(paymentStatus)}`);

                    if (paymentStatus.confirmed) {
                        isWatermarked = false;
                    } else {
                        console.warn(`[ResumeDownload] Payment not confirmed yet. Status: ${paymentStatus.status}`);
                        return NextResponse.json(
                            { error: `Pagamento ainda não confirmado. Status: ${paymentStatus.status || 'Desconhecido'}` },
                            { status: 400 }
                        );
                    }
                } else {
                    // It's a free usage ID (UUID)
                    // If we found the record (Step 1), it is authorized.
                    console.log(`[ResumeDownload] Authorized Free Usage ID: ${paymentId}`);
                    isWatermarked = false;
                }

                if (!isWatermarked) {
                    // 3. Update/Insert Download Record
                    if (existingRecord) {
                        // Update count
                        await supabaseAdmin
                            .from('payment_downloads')
                            .update({
                                download_count: existingRecord.download_count + 1,
                                last_download_at: new Date().toISOString()
                            })
                            .eq('id', existingRecord.id);
                    } else {
                        // Insert new record
                        const { error: insertError } = await supabaseAdmin
                            .from('payment_downloads')
                            .insert({
                                payment_id: paymentId,
                                download_count: 1,
                                max_downloads: 1, // Enforce 1 download
                                created_at: new Date().toISOString(),
                                last_download_at: new Date().toISOString()
                            });

                        if (insertError) console.error('[Supabase] Error inserting record:', insertError);
                    }
                }
            } catch (error) {
                console.error('Error verifying payment/security:', error);
                return NextResponse.json(
                    { error: 'Erro ao verificar pagamento. Tente novamente.' },
                    { status: 500 }
                );
            }
        }

        // Render PDF using React.createElement instead of JSX to avoid parsing issues
        // if the file is treated as .ts by mistake
        // @ts-ignore
        const stream = await renderToStream(
            React.createElement(ResumePDF, {
                data: resumeData,
                template: template,
                isWatermarked: isWatermarked
            })
        );

        return new NextResponse(stream as unknown as BodyInit, {
            headers: {
                'Content-Type': 'application/pdf',
                'Content-Disposition': `attachment; filename="curriculo-${isWatermarked ? 'preview' : 'final'}.pdf"`,
            },
        });

    } catch (error: any) {
        console.error('PDF Generation Error:', error);
        return NextResponse.json(
            { error: 'Erro ao gerar PDF: ' + error.message },
            { status: 500 }
        );
    }
}

import { createClient } from '@supabase/supabase-js';
import { NextResponse } from 'next/server';
import { loadEnvConfig } from '@next/env';

// Force load env vars from project root
loadEnvConfig(process.cwd());

const ASAAS_ACCESS_TOKEN = process.env.ASAAS_API_KEY; // Using the main key to verify if needed, or simply trusting the secret setup in Asaas

export async function POST(request: Request) {
    try {
        const body = await request.json();
        const { event, payment } = body;

        console.log('Webhook Received:', event, payment?.id);

        // Basic verification: Check if it's a payment event we care about
        if (event !== 'PAYMENT_CONFIRMED' && event !== 'PAYMENT_RECEIVED') {
            // We acknowledge other events but don't act
            return NextResponse.json({ received: true });
        }

        if (!payment) {
            return NextResponse.json({ error: 'Invalid payload' }, { status: 400 });
        }

        // Setup Supabase Admin Client
        const supabaseUrl = process.env.SUPABASE_URL;
        const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

        if (!supabaseUrl || !supabaseServiceKey) {
            console.error('Missing Supabase credentials for Webhook');
            return NextResponse.json({ error: 'Server configuration error' }, { status: 500 });
        }

        const supabase = createClient(supabaseUrl, supabaseServiceKey, {
            auth: {
                autoRefreshToken: false,
                persistSession: false
            }
        });

        // Find the lead associated with this payment
        // We can match by asaas_customer_id or asaas_subscription_id if available
        // Payment object usually has: customer, subscription (optional)

        let query = supabase.from('boost_leads').select('id');

        if (payment.subscription) {
            query = query.eq('asaas_subscription_id', payment.subscription);
        } else if (payment.customer) {
            query = query.eq('asaas_customer_id', payment.customer);
        } else {
            // Fallback: try to match by email if available in payment? Not reliable.
            console.error('Webhook: No subscription or customer ID in payment payload', payment.id);
            return NextResponse.json({ received: true, warning: 'No identifier found' });
        }

        const { data: leads, error: searchError } = await query;

        if (searchError || !leads || leads.length === 0) {
            console.error('Webhook: Lead not found for payment', payment.id);
            return NextResponse.json({ received: true, warning: 'Lead not found' });
        }

        const leadId = leads[0].id;

        // Update status to active
        const { error: updateError } = await supabase
            .from('boost_leads')
            .update({
                status: 'active',
                updated_at: new Date().toISOString()
            })
            .eq('id', leadId);

        if (updateError) {
            console.error('Webhook: Failed to update lead status', updateError);
            return NextResponse.json({ error: 'Failed to update lead' }, { status: 500 });
        }

        console.log(`Webhook: Lead ${leadId} activated successfully via payment ${payment.id}`);
        return NextResponse.json({ success: true });

    } catch (error: any) {
        console.error('Webhook Error:', error);
        return NextResponse.json({ error: error.message || 'Internal Error' }, { status: 500 });
    }
}

