                    </div>

                    {/* EDITOR AREA */}
                    <div className="flex-grow relative rounded-xl overflow-hidden border border-white/10 bg-[#050505]">
                        <textarea
                            value={input}
                            onChange={(e) => { setInput(e.target.value); setError(null); }}
                            className="w-full h-full bg-transparent p-6 font-mono text-sm text-slate-300 outline-none resize-none leading-relaxed"
                            placeholder='Paste your JSON here... {"foo": "bar"}'
                            spellCheck={false}
                        />

                        {/* ERROR TOAST */}
                        {error && (
                            <div className="absolute bottom-6 left-6 right-6 bg-red-500/10 border border-red-500/30 p-4 rounded-xl flex items-center gap-3 text-red-500 backdrop-blur-md animate-in fade-in slide-in-from-bottom-4">
                                <AlertTriangle size={18} />
                                <span className="text-xs font-mono font-bold line-clamp-1">{error}</span>
                            </div>
                        )}
                    </div>

                    <div className="mt-4 flex justify-between text-[10px] uppercase font-bold text-slate-600">
                        <span>Lines: {input.split('\n').length}</span>
                        <span>Length: {input.length} chars</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { ShieldCheck, AlertTriangle, Code2, Copy, Check } from 'lucide-react';
import Link from 'next/link';
import { Logo } from '@/components/Logo';
import jwt from 'jsonwebtoken';

function safeJsonParse(str: string) {
    try {
        return JSON.stringify(JSON.parse(str), null, 2);
    } catch {
        return str;
    }
}

export default function JwtDebuggerPage() {
    const [token, setToken] = useState('');
    const [header, setHeader] = useState('');
    const [payload, setPayload] = useState('');
    const [isValid, setIsValid] = useState(false);
    const [copiedHeader, setCopiedHeader] = useState(false);
    const [copiedPayload, setCopiedPayload] = useState(false);

    const copyToClipboard = (text: string, section: 'header' | 'payload') => {
        if (!text) return;
        navigator.clipboard.writeText(text);
        if (section === 'header') {
            setCopiedHeader(true);
            setTimeout(() => setCopiedHeader(false), 2000);
        } else {
            setCopiedPayload(true);
            setTimeout(() => setCopiedPayload(false), 2000);
        }
    };

    useEffect(() => {
        if (!token) {
            setHeader('');
            setPayload('');
            setIsValid(false);
            return;
        }

        try {
            const decoded = jwt.decode(token, { complete: true });
            if (decoded) {
                setHeader(JSON.stringify(decoded.header, null, 2));
                setPayload(JSON.stringify(decoded.payload, null, 2));
                setIsValid(true);
            } else {
                throw new Error('Invalid Token');
            }
        } catch {
            setHeader('Error decoding header');
            setPayload('Error decoding payload');
            setIsValid(false);
        }
    }, [token]);

    return (
        <div className="bg-[#050505] min-h-screen text-slate-300 font-sans flex flex-col items-center justify-center p-6 relative overflow-hidden">
            <div className="absolute inset-0 z-0">
                <div className="absolute top-0 w-full h-[400px] bg-gradient-to-b from-[#A451FF]/10 to-transparent pointer-events-none" />
            </div>

            <div className="max-w-6xl w-full relative z-10 my-10 md:my-20">
                <div className="text-center mb-10">
                    <div className="flex justify-center mb-6">
                        <Logo className="w-16 h-16 md:w-20 md:h-20" />
                    </div>
                    <Link href="/ferramentas" className="text-xs font-bold uppercase tracking-widest text-slate-500 hover:text-white transition-colors mb-4 block">
                        ← Voltar para Ferramentas
                    </Link>
                    <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full border border-white/10 bg-white/5 mb-6 backdrop-blur-md">
                        <Code2 size={12} className="text-[#A451FF]" />
                        <span className="text-[10px] font-bold uppercase tracking-[0.3em] text-[#A451FF]">Token Inspector</span>
                    </div>
                    <h1 className="text-4xl md:text-5xl font-black uppercase italic tracking-tighter text-white mb-2">
                        JWT <span className="text-[#A451FF]">Debugger.</span>
                    </h1>
                    <p className="text-slate-400 text-sm">Decodifique e inspecione tokens JWT com facilidade. (Client-side only)</p>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">

                    {/* INPUT SIDE */}
                    <div className="bg-[#0E0E0E] border border-white/5 rounded-[30px] p-8 shadow-2xl h-full flex flex-col">
                        <h3 className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-4 flex items-center justify-between">
                            <span>Encoded Token</span>
                            {token && (
                                isValid ? (
                                    <span className="text-green-500 flex items-center gap-1"><ShieldCheck size={14} /> Valid Format</span>
                                ) : (
                                    <span className="text-red-500 flex items-center gap-1"><AlertTriangle size={14} /> Invalid</span>
                                )
                            )}
                        </h3>
                        <textarea
                            value={token}
                            onChange={(e) => setToken(e.target.value)}
                            className={`w-full h-[200px] md:h-[400px] bg-[#050505] border rounded-xl p-4 font-mono text-sm outline-none resize-none transition-colors ${isValid ? 'border-[#A451FF] text-[#A451FF]' : 'border-white/10 text-slate-400'}`}
                            placeholder="Cole seu JWT aqui (eyJhbGciOi...)"
                        />
                    </div>

                    {/* OUTPUT SIDE */}
                    <div className="space-y-6">

                        {/* HEADER */}
                        <div className="bg-[#0E0E0E] border border-white/5 rounded-[30px] p-8 shadow-2xl relative overflow-hidden group">
                            <div className="absolute top-0 left-0 w-1 h-full bg-red-500" />
                            <div className="flex justify-between items-center mb-4">
                                <h3 className="text-xs font-bold uppercase tracking-widest text-red-500">Header</h3>
                                {header && (
                                    <button
                                        onClick={() => copyToClipboard(header, 'header')}
                                        className="text-slate-500 hover:text-white transition-colors"
                                    >
                                        {copiedHeader ? <Check size={14} className="text-green-500" /> : <Copy size={14} />}
                                    </button>
                                )}
                            </div>
                            <pre className="font-mono text-xs text-slate-300 overflow-auto max-h-[150px]">
                                {header || <span className="text-slate-700">// Header data...</span>}
                            </pre>
                        </div>

                        {/* PAYLOAD */}
                        <div className="bg-[#0E0E0E] border border-white/5 rounded-[30px] p-8 shadow-2xl relative overflow-hidden group">
                            <div className="absolute top-0 left-0 w-1 h-full bg-[#A451FF]" />
                            <div className="flex justify-between items-center mb-4">
                                <h3 className="text-xs font-bold uppercase tracking-widest text-[#A451FF]">Payload</h3>
                                {payload && (
                                    <button
                                        onClick={() => copyToClipboard(payload, 'payload')}
                                        className="text-slate-500 hover:text-white transition-colors"
                                    >
                                        {copiedPayload ? <Check size={14} className="text-green-500" /> : <Copy size={14} />}
                                    </button>
                                )}
                            </div>
                            <pre className="font-mono text-xs text-slate-300 overflow-auto max-h-[300px]">
                                {payload || <span className="text-slate-700">// Payload data...</span>}
                            </pre>
                        </div>

                        <div className="bg-[#0E0E0E] border border-white/5 rounded-[30px] p-8 shadow-2xl relative overflow-hidden">
                            <div className="absolute top-0 left-0 w-1 h-full bg-[#00F26B]" />
                            <h3 className="text-xs font-bold uppercase tracking-widest text-[#00F26B] mb-4">Signature</h3>
                            <p className="font-mono text-xs text-slate-500">
                                HMACSHA256(
                                base64UrlEncode(header) + "." +
                                base64UrlEncode(payload),
                                secret)
                            </p>
                        </div>

                    </div>

                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState } from 'react';
import { Fingerprint, Copy, Check, Hash } from 'lucide-react';
import Link from 'next/link';
import { Logo } from '@/components/Logo';
import CryptoJS from 'crypto-js';

export default function MD5Page() {
    const [input, setInput] = useState('');
    const [hash, setHash] = useState('');
    const [copied, setCopied] = useState(false);

    const handleCalculate = () => {
        // MD5 is a legacy hash, but still useful for checksums
        if (!input) return;
        const result = CryptoJS.MD5(input).toString();
        setHash(result);
    };

    const copyToClipboard = () => {
        navigator.clipboard.writeText(hash);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    return (
        <div className="bg-[#050505] min-h-screen text-slate-300 font-sans flex flex-col items-center justify-center p-6 relative overflow-hidden">
            <div className="absolute inset-0 z-0">
                <div className="absolute top-[20%] right-[30%] w-[30vw] h-[30vw] bg-red-500/5 blur-[120px] rounded-full pointer-events-none" />
            </div>

            <div className="max-w-3xl w-full relative z-10 my-10 md:my-20">
                <div className="text-center mb-10">
                    <div className="flex justify-center mb-6">
                        <Logo className="w-16 h-16 md:w-20 md:h-20" />
                    </div>
                    <Link href="/ferramentas" className="text-xs font-bold uppercase tracking-widest text-slate-500 hover:text-white transition-colors mb-4 block">
                        ← Voltar para Ferramentas
                    </Link>
                    <h1 className="text-4xl md:text-5xl font-black uppercase italic tracking-tighter text-white mb-2">
                        Gerador <span className="text-red-500">MD5.</span>
                    </h1>
                    <p className="text-slate-500 text-xs uppercase tracking-widest mt-2">Legacy Message-Digest Algorithm 5</p>
                </div>

                <div className="bg-[#0E0E0E] border border-white/5 rounded-[30px] p-6 md:p-8 shadow-2xl">
                    <div className="mb-6">
                        <label className="block text-xs font-bold text-slate-500 mb-2 uppercase tracking-wide">Texto para Hash</label>
                        <input
                            type="text"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyUp={(e) => handleCalculate()}
                            className="w-full bg-[#050505] border border-white/10 rounded-xl px-4 py-4 text-white focus:border-red-500 outline-none transition-colors font-mono text-sm"
                            placeholder="Digite algo..."
                        />
                    </div>

                    <div className="bg-[#050505] p-6 rounded-xl border border-white/5 relative group hover:border-red-500/30 transition-colors">
                        <div className="flex justify-between items-center mb-2">
                            <div className="text-[10px] font-bold uppercase tracking-widest text-slate-500 flex items-center gap-2">
                                <Hash size={12} className="text-red-500" /> Hash Result
                            </div>
                            {hash && (
                                <button onClick={copyToClipboard} className="text-xs text-red-500 hover:text-white flex items-center gap-1 transition-colors">
                                    {copied ? <Check size={14} /> : <Copy size={14} />}
                                </button>
                            )}
                        </div>
                        <div className="font-mono text-xl md:text-2xl text-white break-all tracking-tight min-h-[2rem]">
                            {hash || <span className="text-slate-700 select-none">...</span>}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Smartphone, Monitor, Cpu, Chrome, Globe, Layout } from 'lucide-react';
import Link from 'next/link';
import { Logo } from '@/components/Logo';

export default function MyDevicePage() {
    const [info, setInfo] = useState<any>(null);

    useEffect(() => {
        // Simple User Agent Parsing (Client Side Only)
        const ua = navigator.userAgent;
        let browser = "Unknown";
        let os = "Unknown";
        let device = "Desktop";

        // Browser Detection
        if (ua.indexOf("Firefox") > -1) browser = "Mozilla Firefox";
        else if (ua.indexOf("SamsungBrowser") > -1) browser = "Samsung Internet";
        else if (ua.indexOf("Opera") > -1 || ua.indexOf("OPR") > -1) browser = "Opera";
        else if (ua.indexOf("Trident") > -1) browser = "Microsoft Internet Explorer";
        else if (ua.indexOf("Edge") > -1) browser = "Microsoft Edge";
        else if (ua.indexOf("Chrome") > -1) browser = "Google Chrome";
        else if (ua.indexOf("Safari") > -1) browser = "Apple Safari";

        // OS Detection
        if (ua.indexOf("Win") > -1) os = "Windows";
        else if (ua.indexOf("Mac") > -1) os = "MacOS";
        else if (ua.indexOf("Linux") > -1) os = "Linux";
        else if (ua.indexOf("Android") > -1) os = "Android";
        else if (ua.indexOf("like Mac") > -1) os = "iOS";

        // Device Detection
        if (/Mobi|Android/i.test(ua)) device = "Mobile";

        setInfo({
            userAgent: ua,
            browser,
            os,
            device,
            screen: `${window.screen.width} x ${window.screen.height}`,
            depth: `${window.screen.colorDepth}-bit`,
            cores: navigator.hardwareConcurrency ? `${navigator.hardwareConcurrency} Logical Cores` : 'Unknown',
            memory: (navigator as any).deviceMemory ? `~${(navigator as any).deviceMemory} GB` : 'Unknown',
            language: navigator.language,
            cookies: navigator.cookieEnabled ? "Enabled" : "Disabled"
        });
    }, []);

    const InfoCard = ({ icon: Icon, label, value, color = "#A451FF", full = false }: any) => (
        <div className={`bg-[#0E0E0E] border border-white/5 p-8 rounded-2xl flex flex-col justify-between hover:border-white/10 transition-colors ${full ? 'md:col-span-2 lg:col-span-3' : ''}`}>
            <div className="flex justify-between items-start mb-4">
                <div className={`w-12 h-12 rounded-xl bg-white/5 flex items-center justify-center`} style={{ color }}>
                    <Icon size={24} />
                </div>
                <div className="text-[10px] font-bold uppercase tracking-widest text-slate-500">{label}</div>
            </div>
            <div className={`${full ? 'font-mono text-xs text-slate-400 break-all' : 'text-2xl font-bold text-white tracking-tight'}`}>
                {value || 'Loading...'}
            </div>
        </div>
    );

    return (
        <div className="bg-[#050505] min-h-screen text-slate-300 font-sans flex flex-col items-center justify-center p-6 relative overflow-hidden">
            <div className="absolute inset-0 z-0">
                <div className="absolute top-[20%] right-[-10%] w-[50vw] h-[50vw] bg-[#A451FF]/5 blur-[150px] rounded-full pointer-events-none" />
                <div className="absolute bottom-[20%] left-[-10%] w-[50vw] h-[50vw] bg-[#00F26B]/5 blur-[150px] rounded-full pointer-events-none" />
            </div>

            <div className="max-w-5xl w-full relative z-10 my-10 md:my-20">
                <div className="text-center mb-12">
                    <div className="flex justify-center mb-6">
                        <Logo className="w-16 h-16 md:w-20 md:h-20" />
                    </div>
                    <Link href="/ferramentas" className="text-xs font-bold uppercase tracking-widest text-slate-500 hover:text-white transition-colors mb-4 block">
                        ← Voltar para Ferramentas
                    </Link>
                    <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full border border-[#A451FF]/20 bg-[#A451FF]/5 mb-6 backdrop-blur-md">
                        <Smartphone size={12} className="text-[#A451FF]" />
                        <span className="text-[10px] font-bold uppercase tracking-[0.3em] text-[#A451FF]">System Info</span>
                    </div>
                    <h1 className="text-4xl md:text-5xl font-black uppercase italic tracking-tighter text-white mb-2">
                        Meu <span className="text-[#A451FF]">Dispositivo.</span>
                    </h1>
                    <p className="text-slate-400 text-sm">Informações de hardware e software extraídas do seu navegador.</p>
                </div>

                {info && (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        <InfoCard icon={Chrome} label="Navegador" value={info.browser} color="#00F26B" />
                        <InfoCard icon={Layout} label="Sistema Operacional" value={info.os} color="#A451FF" />
                        <InfoCard icon={info.device === 'Mobile' ? Smartphone : Monitor} label="Tipo de Dispositivo" value={info.device} color="#FFD700" />

                        <InfoCard icon={Monitor} label="Resolução de Tela" value={info.screen} color="#00CCFF" />
                        <InfoCard icon={Cpu} label="CPU Cores" value={info.cores} color="#FF0055" />
                        <InfoCard icon={Globe} label="Idioma" value={info.language} color="#FFA500" />

                        <InfoCard icon={Cpu} label="User Agent String" value={info.userAgent} color="#888888" full={true} />
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Globe, MapPin, Server, Shield, Wifi, RefreshCw } from 'lucide-react';
import Link from 'next/link';
import { Logo } from '@/components/Logo';

interface IpData {
    ip: string;
    city?: string;
    region?: string;
    country_name?: string;
    org?: string;
    asn?: string;
    timezone?: string;
    latitude?: number;
    longitude?: number;
}

export default function MyIpPage() {
    const [data, setData] = useState<IpData | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    const fetchIp = async () => {
        setLoading(true);
        setError(false);
        try {
            const res = await fetch('https://ipapi.co/json/');
            if (!res.ok) throw new Error('Failed to fetch');
            const json = await res.json();
            setData(json);
        } catch (err) {
            setError(true);
            // Fallback for just IP if detailed API fails
            try {
                const res2 = await fetch('https://api.ipify.org?format=json');
                const json2 = await res2.json();
                setData({ ip: json2.ip });
            } catch {
                setData(null);
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchIp();
    }, []);

    const InfoCard = ({ icon: Icon, label, value, color = "#A451FF" }: any) => (
        <div className="bg-[#0E0E0E] border border-white/5 p-6 rounded-2xl flex items-center gap-4 hover:border-white/10 transition-colors">
            <div className={`w-12 h-12 rounded-xl bg-white/5 flex items-center justify-center`} style={{ color }}>
                <Icon size={24} />
            </div>
            <div>
                <div className="text-[10px] font-bold uppercase tracking-widest text-slate-500 mb-1">{label}</div>
                <div className="text-white font-mono text-sm md:text-base break-all">
                    {loading ? <span className="animate-pulse bg-white/10 text-transparent rounded">Loading...</span> : (value || 'N/A')}
                </div>
            </div>
        </div>
    );

    return (
        <div className="bg-[#050505] min-h-screen text-slate-300 font-sans flex flex-col items-center justify-center p-6 relative overflow-hidden">
            <div className="absolute inset-0 z-0">
                <div className="absolute top-[-20%] left-[-10%] w-[60vw] h-[60vw] bg-[#A451FF]/5 blur-[150px] rounded-full pointer-events-none" />
                <div className="absolute bottom-[-20%] right-[-10%] w-[60vw] h-[60vw] bg-[#00F26B]/5 blur-[150px] rounded-full pointer-events-none" />
            </div>

            <div className="max-w-5xl w-full relative z-10 my-10 md:my-20">
                <div className="text-center mb-12">
                    <div className="flex justify-center mb-6">
                        <Logo className="w-16 h-16 md:w-20 md:h-20" />
                    </div>
                    <Link href="/ferramentas" className="text-xs font-bold uppercase tracking-widest text-slate-500 hover:text-white transition-colors mb-4 block">
                        ← Voltar para Ferramentas
                    </Link>
                    <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full border border-[#00F26B]/20 bg-[#00F26B]/5 mb-6 backdrop-blur-md">
                        <Globe size={12} className="text-[#00F26B]" />
                        <span className="text-[10px] font-bold uppercase tracking-[0.3em] text-[#00F26B]">Network Diagnostic</span>
                    </div>
                    <h1 className="text-4xl md:text-5xl font-black uppercase italic tracking-tighter text-white mb-2">
                        Meu <span className="text-[#00F26B]">Endereço IP.</span>
                    </h1>
                    <p className="text-slate-400 text-sm">Visualize informações detalhadas sobre sua conexão atual.</p>
                </div>

                {/* MAIN IP DISPLAY */}
                <div className="max-w-2xl mx-auto mb-12">
                    <div className="bg-[#0E0E0E] border border-white/5 rounded-[40px] p-8 md:p-12 text-center relative overflow-hidden group">
                        <div className="absolute inset-0 bg-gradient-to-r from-[#00F26B]/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity" />

                        <div className="relative z-10">
                            <div className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-4">Seu IP Público IPv4</div>
                            <div className="text-4xl md:text-6xl font-black text-white font-mono tracking-tighter mb-8 break-all">
                                {loading ? <span className="animate-pulse">Loading...</span> : (data?.ip || 'Unavailable')}
                            </div>

                            <button
                                onClick={fetchIp}
                                className="inline-flex items-center gap-2 px-6 py-3 bg-[#00F26B] hover:bg-[#00c957] text-black font-bold uppercase tracking-widest rounded-xl transition-all shadow-[0_0_20px_rgba(0,242,107,0.3)] hover:shadow-[0_0_30px_rgba(0,242,107,0.5)] text-xs"
                            >
                                <RefreshCw size={14} className={loading ? "animate-spin" : ""} /> Atualizar Dados
                            </button>
                        </div>
                    </div>
                </div>

                {/* GRID DETAILS */}
                {!loading && data && !error && (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        <InfoCard icon={MapPin} label="Localização" value={`${data.city || ''}, ${data.region || ''} - ${data.country_name || ''}`} color="#A451FF" />
                        <InfoCard icon={Server} label="Provedor (ISP)" value={data.org} color="#00F26B" />
