"use client";

import { useLoader } from "@react-three/fiber";
// @ts-ignore
import { SVGLoader } from "three/examples/jsm/loaders/SVGLoader";
import { TextureLoader } from "three";
import { useMemo } from "react";
import * as THREE from "three";

interface LogoModelProps {
    fileUrl: string | null;
    fileType: "svg" | "png" | null;
    color: string;
    metalness: number;
    roughness: number;
    intensity: number;
    depth: number;
}

// Sub-component for SVG - ensures hook is called effectively unconditionally within this component
function SvgLogo({ url, material, depth }: { url: string; material: THREE.Material, depth: number }) {
    const svgData = useLoader(SVGLoader, url);

    const shapes = useMemo(() => {
        return svgData.paths.flatMap((path: any) =>
            path.toShapes(true).map((shape: any) => ({ shape, color: path.color }))
        );
    }, [svgData]);

    return (
        <group scale={0.01} rotation={[Math.PI, 0, 0]}>
            {/* Center the group roughly */}
            <group position={[-50, -50, 0]}>
                {shapes.map((item: any, index: number) => (
                    <mesh key={index} material={material} position={[0, 0, 0]}>
                        <extrudeGeometry
                            args={[
                                item.shape,
                                {
                                    depth: depth, // Dynamic depth
                                    bevelEnabled: true,
                                    bevelThickness: 0.5,
                                    bevelSize: 0.5,
                                    bevelSegments: 10,
                                },
                            ]}
                        />
                    </mesh>
                ))}
            </group>
        </group>
    );
}

// Sub-component for PNG
function PngLogo({ url, color, metalness, roughness, intensity, depth }: { url: string; color: string; metalness: number; roughness: number; intensity: number; depth: number }) {
    const texture = useLoader(TextureLoader, url);
    // Calculate aspect ratio
    const aspect = texture.image.width / texture.image.height;
    // Base size
    const width = 3;
    const height = width / aspect;

    // Create a specific material for PNG
    const pngMaterial = useMemo(() => new THREE.MeshPhysicalMaterial({
        map: texture,
        color: color,
        metalness: metalness,
        roughness: roughness,
        envMapIntensity: intensity,
        transparent: true,
        side: THREE.DoubleSide,
    }), [texture, color, metalness, roughness, intensity]);

    return (
        <mesh material={pngMaterial}>
            {/* Box with dynamic depth */}
            <boxGeometry args={[width, height, depth * 0.1]} />
        </mesh>
    );
}

export default function LogoModel({
    fileUrl,
    fileType,
    color,
    metalness,
    roughness,
    intensity,
    depth = 2, // Default depth
}: LogoModelProps) {
    // Shared material for generic shapes or SVG fallback
    const sharedMaterial = useMemo(
        () =>
            new THREE.MeshPhysicalMaterial({
                color: color,
                metalness: metalness,
                roughness: roughness,
                envMapIntensity: intensity,
                clearcoat: 1,
                clearcoatRoughness: 0.1,
            }),
        [color, metalness, roughness, intensity]
    );

    if (fileType === "svg" && fileUrl) {
        return <SvgLogo url={fileUrl} material={sharedMaterial} depth={depth} />;
    }

    if (fileType === "png" && fileUrl) {
        return <PngLogo url={fileUrl} color={color} metalness={metalness} roughness={roughness} intensity={intensity} depth={depth} />;
    }

    // Fallback / Placeholder
    return (
        <mesh material={sharedMaterial}>
        </mesh>
    );
}

"use client";

import { Canvas } from "@react-three/fiber";
import { Environment, OrbitControls, Center, Stage } from "@react-three/drei";
import LogoModel from "./LogoModel";
import { forwardRef } from "react";

interface SceneProps {
    fileUrl: string | null;
    fileType: "svg" | "png" | null;
    materialProps: {
        color: string;
        metalness: number;
        roughness: number;
        intensity: number;
        depth: number;
    };
    showGrid: boolean;
    autoRotate: boolean;
    transparentImg: boolean;
}

// Forward ref to allow parent to access the canvas (for screenshots)
// Added modelRef prop for GLTF export
const Scene = forwardRef<HTMLCanvasElement, SceneProps & { modelRef: React.RefObject<any> }>(({ fileUrl, fileType, materialProps, modelRef, showGrid, autoRotate, transparentImg }, ref) => {
    return (
        <Canvas
            ref={ref}
            shadows
            gl={{ preserveDrawingBuffer: true, antialias: true, alpha: true }}
            camera={{ position: [0, 10, 20], fov: 45 }}
            className={`w-full h-full rounded-xl ${transparentImg ? '' : 'bg-neutral-900/50'}`}
        >
            <ambientLight intensity={0.5} />
            <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} />

            <Center top>
                <group ref={modelRef}>
                    <LogoModel
                        fileUrl={fileUrl}
                        fileType={fileType}
                        {...materialProps}
                    />
                </group>
            </Center>

            {/* 3D Backdrop Floor - Conditionally rendered */}
            {showGrid && !transparentImg && (
                <>
                    <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -0.1, 0]} receiveShadow>
                        <planeGeometry args={[100, 100]} />
                        <meshStandardMaterial color="#111111" roughness={0.5} metalness={0.8} />
                    </mesh>
                    <gridHelper args={[100, 100, 0x444444, 0x111111]} position={[0, -0.09, 0]} />
                </>
            )}

            {/* Environment: Background prop controls visibility of the HDRI background */}
            <Environment preset="city" background={!transparentImg} blur={0.8} />
            {/* Free movement enabled (removed polar limits), added autoRotate */}
            <OrbitControls makeDefault autoRotate={autoRotate} />
        </Canvas>
    );
});

Scene.displayName = "Scene";

export default Scene;

"use client";

import { useState, useRef, useCallback } from "react";
import Scene from "./Scene";
import { Upload, Download, Scan, Image as ImageIcon } from "lucide-react";
import { Slider } from "./ui/Slider";
import { ColorPicker } from "./ui/ColorPicker";

export default function Visualizer() {
    const [fileUrl, setFileUrl] = useState<string | null>("/logo.svg"); // Default to Hoffby logo
    const [fileType, setFileType] = useState<"svg" | "png" | null>("svg"); // Default type

    // Scene Settings
    const [showGrid, setShowGrid] = useState(true);
    const [autoRotate, setAutoRotate] = useState(false);
    const [transparentImg, setTransparentImg] = useState(false);

    const [materialProps, setMaterialProps] = useState({
        color: "#ffffff",
        metalness: 0.9,
        roughness: 0.1,
        intensity: 1.0,
        depth: 2, // New depth property
    });

    const canvasRef = useRef<HTMLCanvasElement>(null);
    const modelRef = useRef<any>(null);

    const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        // Revoke old URL to avoid memory leaks if it was a blob
        if (fileUrl && fileUrl.startsWith("blob:")) URL.revokeObjectURL(fileUrl);

        const url = URL.createObjectURL(file);
        setFileUrl(url);

        if (file.type === "image/svg+xml") {
            setFileType("svg");
        } else if (file.type === "image/png") {
            setFileType("png");
        } else {
            alert("Please upload SVG or PNG");
        }
    };

    const updateMaterial = (key: keyof typeof materialProps, value: string | number) => {
        setMaterialProps((prev) => ({ ...prev, [key]: value }));
    };

    const handleExportPNG = useCallback(() => {
        if (canvasRef.current) {
            // The canvas ref from R3F might point to the wrapper div or the canvas element depending on version
            // Actually, we forwarded the ref to the Canvas component.
            // Let's ensure we get the canvas DOM element.
            // Simple trick: triggering a download with the data URL
            const link = document.createElement("a");
            link.setAttribute("download", "logo-3d.png");
            link.setAttribute("href", canvasRef.current.toDataURL("image/png").replace("image/png", "image/octet-stream"));
            link.click();
        }
    }, []);

    const handleExportGLTF = useCallback(() => {
        import("three/examples/jsm/exporters/GLTFExporter.js").then(({ GLTFExporter }) => {
            const exporter = new GLTFExporter();
            if (modelRef.current) {
                exporter.parse(
                    modelRef.current,
                    (gltf) => {
                        const output = JSON.stringify(gltf, null, 2);
                        const blob = new Blob([output], { type: "text/plain" });
                        const url = URL.createObjectURL(blob);
                        const link = document.createElement("a");
                        link.style.display = "none";
                        link.href = url;
                        link.download = "logo-3d.gltf";
                        document.body.appendChild(link);
                        link.click();
                        document.body.removeChild(link);
                    },
                    (error) => {
                        console.error("An error happened during GLTF export:", error);
                        alert("Error exporting GLTF. Check console.");
                    },
                    { binary: false } // Export as .gltf (text) vs .glb (binary) - user requested .gltf
                );
            } else {
                alert("No 3D model found to export.");
            }
        });
    }, []);

    return (
        <div className="flex flex-col lg:flex-row h-[calc(100vh-100px)] gap-6 p-6">
            {/* Controls sidebar content... */}
            <div className="w-full lg:w-1/4 flex flex-col gap-6 bg-neutral-900/80 backdrop-blur-md p-6 rounded-2xl border border-white/10 overflow-y-auto">
                {/* Header and Upload... */}
                <div>
                    <h2 className="text-2xl font-bold bg-gradient-to-r from-white to-neutral-400 bg-clip-text text-transparent mb-2">3D Logoizer</h2>
                    <p className="text-neutral-400 text-sm">Turn SVG logos into 3D metal art.</p>
                </div>

                {/* Upload Zone */}
                <div className="relative group cursor-pointer border-2 border-dashed border-white/20 hover:border-blue-500/50 rounded-xl p-8 transition-all bg-black/20 hover:bg-black/40">
                    <input
                        type="file"
                        accept=".svg"
                        onChange={handleFileUpload}
                        className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                    />
                    <div className="flex flex-col items-center gap-2 text-neutral-400 group-hover:text-white transition-colors">
                        <Upload className="w-8 h-8" />
                        <span className="text-sm font-medium">Drop SVG File</span>
                        <span className="text-[10px] text-neutral-500 uppercase tracking-widest">SVG Only</span>
                    </div>
                </div>

                {/* Material Controls */}
                <div className="space-y-6">
                    <h3 className="text-sm font-semibold text-white/80 uppercase tracking-wider border-b border-white/10 pb-2">Material</h3>

                    <ColorPicker
                        label="Surface Color"
                        color={materialProps.color}
                        onChange={(val) => updateMaterial("color", val)}
                    />

                    <Slider
                        label="Metalness"
                        value={materialProps.metalness}
                        min={0}
                        max={1}
                        step={0.05}
                        onChange={(e) => updateMaterial("metalness", parseFloat(e.target.value))}
                        valueDisplay={`${Math.round(materialProps.metalness * 100)}%`}
                    />

                    <Slider
                        label="Roughness"
                        value={materialProps.roughness}
                        min={0}
                        max={1}
                        step={0.05}
                        onChange={(e) => updateMaterial("roughness", parseFloat(e.target.value))}
                        valueDisplay={`${Math.round(materialProps.roughness * 100)}%`}
                    />

                    <Slider
                        label="Reflection Intensity"
                        value={materialProps.intensity}
                        min={0}
                        max={5}
