import React, { useState, useEffect, useRef } from 'react';
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon } from 'lucide-react';

interface MonthYearPickerProps {
    value: string; // Format: "YYYY-MM"
    onChange: (value: string) => void;
    label?: string;
    disabled?: boolean;
}

const MONTHS = [
    "Jan", "Fev", "Mar", "Abr", "Mai", "Jun",
    "Jul", "Ago", "Set", "Out", "Nov", "Dez"
];

export function MonthYearPicker({ value, onChange, label, disabled }: MonthYearPickerProps) {
    const [isOpen, setIsOpen] = useState(false);
    const [viewYear, setViewYear] = useState(new Date().getFullYear());
    const containerRef = useRef<HTMLDivElement>(null);

    // Initialize viewYear from value if present
    useEffect(() => {
        if (value) {
            const [y] = value.split('-');
            if (y) setViewYear(parseInt(y));
        }
    }, [value]);

    // Close on click outside
    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
                setIsOpen(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleMonthSelect = (monthIndex: number) => {
        const monthStr = (monthIndex + 1).toString().padStart(2, '0');
        onChange(`${viewYear}-${monthStr}`);
        setIsOpen(false);
    };

    const formatDisplay = (val: string) => {
        if (!val) return 'Selecione a data';
        const [y, m] = val.split('-');
        if (!m) return y; // Return just year if no month

        const monthIndex = parseInt(m) - 1;
        if (isNaN(monthIndex) || monthIndex < 0 || monthIndex >= 12) return val; // Fallback to raw value

        const monthName = MONTHS[monthIndex];
        return `${monthName} ${y}`;
    };

    return (
        <div className="relative w-full" ref={containerRef}>
            {label && <label className="block text-xs font-bold uppercase tracking-wider text-slate-400 mb-2.5 ml-1">{label}</label>}

            <button
                type="button"
                onClick={() => !disabled && setIsOpen(!isOpen)}
                disabled={disabled}
                className={`w-full bg-[#050505] border border-white/10 rounded-xl px-4 py-3.5 text-white flex items-center justify-between transition-all hover:border-emerald-500/30 ${disabled ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'} ${isOpen ? 'border-emerald-500/50 ring-1 ring-emerald-500/50' : ''}`}
            >
                <span className={`text-sm ${value ? 'text-white' : 'text-slate-500'}`}>
                    {value ? formatDisplay(value) : 'MM / AAAA'}
                </span>
                <CalendarIcon size={16} className="text-slate-500" />
            </button>

            {isOpen && (
                <div className="absolute top-[110%] left-0 z-50 w-64 bg-[#0A0A0A] border border-white/10 rounded-xl shadow-2xl p-4 animate-in fade-in zoom-in-95 duration-200">
                    <div className="flex items-center justify-between mb-4 pb-2 border-b border-white/5">
                        <button
                            type="button"
                            onClick={() => setViewYear(prev => prev - 1)}
                            className="p-1 hover:bg-white/10 rounded-lg text-slate-400 hover:text-white transition-colors"
                        >
                            <ChevronLeft size={18} />
                        </button>
                        <span className="font-bold text-white text-lg font-mono">{viewYear}</span>
                        <button
                            type="button"
                            onClick={() => setViewYear(prev => prev + 1)}
                            className="p-1 hover:bg-white/10 rounded-lg text-slate-400 hover:text-white transition-colors"
                        >
                            <ChevronRight size={18} />
                        </button>
                    </div>

                    <div className="grid grid-cols-3 gap-2">
                        {MONTHS.map((month, index) => {
                            const isSelected = value === `${viewYear}-${(index + 1).toString().padStart(2, '0')}`;
                            return (
                                <button
                                    key={month}
                                    type="button"
                                    onClick={() => handleMonthSelect(index)}
                                    className={`py-2 px-1 rounded-lg text-sm font-medium transition-all ${isSelected
                                        ? 'bg-emerald-500 text-white shadow-lg shadow-emerald-500/20'
                                        : 'text-slate-400 hover:bg-white/10 hover:text-white'
                                        }`}
                                >
                                    {month}
                                </button>
                            );
                        })}
                    </div>
                </div>
            )}
        </div>
    );
}

import React, { useEffect, useState } from 'react';
import { Modal } from '../../Modal';
import { QrCode, Copy, Check, Loader2, AlertCircle, ShieldCheck, Lock, Ticket, Tag } from 'lucide-react';
import { ResumeData } from './ResumeGenerator';
import { cleanCPF, formatCPF, isValidCPF } from '@/lib/cpf';

const copyToClipboard = async (text: string) => {
    try {
        await navigator.clipboard.writeText(text);
        return true;
    } catch (err) {
        console.error('Failed to copy', err);
        return false;
    }
};

interface PaymentModalProps {
    isOpen: boolean;
    onClose: () => void;
    onSuccess: (paymentId?: string) => void;
    data: ResumeData;
}

interface PaymentState {
    step: 'cpf' | 'loading' | 'payment' | 'success';
    error: string | null;
    qrCodeImage: string | null;
    qrCodePayload: string | null;
    paymentId: string | null;
}

interface CouponState {
    code: string;
    valid: boolean;
    discountPercent: number;
    message: string | null;
    isValidating: boolean;
}

export function PaymentModal({ isOpen, onClose, onSuccess, data }: PaymentModalProps) {
    const [state, setState] = useState<PaymentState>({
        step: 'cpf',
        error: null,
        qrCodeImage: null,
        qrCodePayload: null,
        paymentId: null
    });
    const [cpf, setCpf] = useState('');
    const [copied, setCopied] = useState(false);

    // Coupon State
    const [coupon, setCoupon] = useState<CouponState>({
        code: '',
        valid: false,
        discountPercent: 0,
        message: null,
        isValidating: false
    });

    // Reset state when opening
    useEffect(() => {
        if (isOpen) {
            setState({
                step: 'cpf',
                error: null,
                qrCodeImage: null,
                qrCodePayload: null,
                paymentId: null
            });
            setCpf('');
            setCoupon({
                code: '',
                valid: false,
                discountPercent: 0,
                message: null,
                isValidating: false
            });
        }
    }, [isOpen]);

    // Polling for Status
    useEffect(() => {
        let interval: NodeJS.Timeout;

        if (isOpen && state.step === 'payment' && state.paymentId) {
            interval = setInterval(checkStatus, 3000);
        }

        return () => clearInterval(interval);
    }, [isOpen, state.step, state.paymentId]);

    const handleValidateCoupon = async () => {
        if (!coupon.code) return;

        setCoupon(prev => ({ ...prev, isValidating: true, message: null, error: null })); // clear previous msg

        try {
            const response = await fetch('/api/payment/validate-coupon', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ code: coupon.code })
            });
            const result = await response.json();

            if (result.valid) {
                setCoupon(prev => ({
                    ...prev,
                    valid: true,
                    discountPercent: result.discountPercent,
                    message: `Cupom aplicado! ${result.discountPercent}% OFF`,
                    isValidating: false
                }));
            } else {
                setCoupon(prev => ({
                    ...prev,
                    valid: false,
                    discountPercent: 0,
                    message: result.message || 'Cupom inválido',
                    isValidating: false
                }));
            }
        } catch (error) {
            setCoupon(prev => ({
                ...prev,
                valid: false,
                discountPercent: 0,
                message: 'Erro ao validar cupom',
                isValidating: false
            }));
        }
    };

    const handleCreatePayment = async () => {
        // 100% Discount Flow
        if (coupon.valid && coupon.discountPercent === 100) {
            setState(prev => ({ ...prev, step: 'loading', error: null }));
            try {
                // Register Usage
                const response = await fetch('/api/payment/register-usage', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ code: coupon.code })
                });

                if (response.ok) {
                    const result = await response.json();
                    setState(prev => ({ ...prev, step: 'success' }));
                    setTimeout(() => {
                        onSuccess(result.paymentId); // Gratis / Cupom com ID de uso
                        onClose();
                    }, 1500);
                } else {
                    setState(prev => ({ ...prev, step: 'cpf', error: 'Erro ao registrar download gratuito.' }));
                }
            } catch (error) {
                setState(prev => ({ ...prev, step: 'cpf', error: 'Erro ao processar cupom.' }));
            }
            return;
        }

        if (!isValidCPF(cpf)) {
            setState(prev => ({ ...prev, error: 'CPF inválido. Verifique os números.' }));
            return;
        }

        // Normal Flow (Partial or No Discount)
        try {
            setState(prev => ({ ...prev, step: 'loading', error: null }));

            const response = await fetch('/api/payment/create', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    name: data.personal.fullName || 'Cliente Hoffby',
                    email: data.personal.email || 'email@exemplo.com',
                    cpf: cleanCPF(cpf),
                    coupon: coupon.valid ? coupon.code : undefined
                })
            });

            const result = await response.json();

            if (!response.ok) {
                throw new Error(result.error || 'Erro ao criar cobrança');
            }

            setState(prev => ({
                ...prev,
                step: 'payment',
                paymentId: result.paymentId,
                qrCodeImage: result.encodedImage,
                qrCodePayload: result.payload
            }));

        } catch (err: any) {
            setState(prev => ({ ...prev, step: 'cpf', error: err.message }));
        }
    };

    const checkStatus = async () => {
        if (!state.paymentId) return;

        try {
            const response = await fetch(`/api/payment/status?id=${state.paymentId}`);
            const result = await response.json();

            if (result.confirmed) {
                setState(prev => ({ ...prev, step: 'success' }));
                setTimeout(() => {
                    onSuccess(state.paymentId!);
                    onClose();
                }, 2000); // Wait 2s to show success message
            }
        } catch (error) {
            console.error('Status check error', error);
        }
    };

    const handleCopy = () => {
        if (state.qrCodePayload) {
            copyToClipboard(state.qrCodePayload);
            setCopied(true);
            setTimeout(() => setCopied(false), 2000);
        }
    };

    // Calculate final price display
    const basePrice = 5.00;
    const finalPrice = coupon.valid ? (basePrice * (1 - coupon.discountPercent / 100)) : basePrice;

    return (
        <Modal
            isOpen={isOpen}
            onClose={onClose}
            title="Pagamento Seguro"
            icon={<Lock size={20} className="text-emerald-400" />}
            maxWidth="max-w-md"
        >
            {state.step === 'cpf' && (
                <div className="space-y-4">
                    <div className="p-4 bg-emerald-500/10 border border-emerald-500/20 rounded-xl flex gap-3">
                        <ShieldCheck className="shrink-0 text-emerald-400" size={24} />
                        <div>
                            <p className="text-emerald-100 font-bold mb-1">Liberação Imediata</p>
                            <p className="text-emerald-200/70 text-sm">
                                Para baixar seu currículo profissional em PDF de alta qualidade, é necessário uma taxa única.
                            </p>
                        </div>
                    </div>

                    {/* Coupon Input - Moved to Top */}
                    <div>
                        <label className="block text-xs font-bold uppercase text-slate-400 mb-2 flex items-center gap-2">
                            <Tag size={12} /> Cupom de Desconto
                        </label>
                        <div className="flex gap-2 mb-2">
                            <input
                                type="text"
                                className={`flex-1 bg-[#050505] border rounded-xl px-4 py-3 text-white focus:outline-none font-mono tracking-wider uppercase text-sm ${coupon.valid ? 'border-emerald-500/50 text-emerald-400' : 'border-white/10'}`}
                                placeholder="CÓDIGO"
                                value={coupon.code}
                                onChange={(e) => setCoupon(prev => ({ ...prev, code: e.target.value.toUpperCase(), valid: false, message: null }))}
                                disabled={coupon.valid && coupon.discountPercent === 100}
                            />
                            <button
                                onClick={handleValidateCoupon}
                                disabled={!coupon.code || coupon.isValidating || (coupon.valid && coupon.discountPercent === 100)}
                                className="px-4 py-2 bg-white/5 hover:bg-white/10 border border-white/10 rounded-xl text-slate-300 font-bold text-xs disabled:opacity-50 transition-colors"
                            >
                                {coupon.isValidating ? <Loader2 size={16} className="animate-spin" /> : 'Aplicar'}
                            </button>
                        </div>
                        {coupon.message && (
                            <p className={`text-xs flex items-center gap-1 mb-4 ${coupon.valid ? 'text-emerald-400' : 'text-red-400'}`}>
                                {coupon.valid ? <Check size={12} /> : <AlertCircle size={12} />} {coupon.message}
                            </p>
                        )}
                    </div>

                    {/* CPF Input - Only shown if not 100% free */}
                    {!(coupon.valid && coupon.discountPercent === 100) && (
                        <div>
                            <label className="block text-xs font-bold uppercase text-slate-400 mb-2">Informe seu CPF (Para o Pix)</label>
                            <input
                                type="text"
                                className="w-full bg-[#050505] border border-white/10 rounded-xl px-4 py-3 text-white focus:outline-none focus:border-emerald-500 font-mono tracking-wider mb-4"
                                placeholder="000.000.000-00"
                                value={cpf}
                                maxLength={14}
                                onChange={(e) => setCpf(formatCPF(e.target.value))}
                            />
                            {state.error && <p className="text-red-400 text-sm mt-2 flex items-center gap-1"><AlertCircle size={14} /> {state.error}</p>}
                        </div>
                    )}

                    <div className="py-4 border-t border-white/5 flex justify-between items-center text-sm">
                        <span className="text-slate-400">Total a pagar:</span>
                        <div className="text-right">
                            {coupon.valid && coupon.discountPercent > 0 && (
                                <span className="block text-xs text-slate-500 line-through">R$ {basePrice.toFixed(2).replace('.', ',')}</span>
                            )}
                            <span className={`font-bold text-lg ${finalPrice === 0 ? 'text-emerald-400' : 'text-white'}`}>
                                {finalPrice === 0 ? 'GRÁTIS' : `R$ ${finalPrice.toFixed(2).replace('.', ',')}`}
                            </span>
                        </div>
                    </div>

                    <button
                        onClick={handleCreatePayment}
                        className={`w-full py-3 font-bold rounded-xl transition-all shadow-lg text-black ${finalPrice === 0 ? 'bg-emerald-400 hover:bg-emerald-500 shadow-emerald-900/20' : 'bg-emerald-500 hover:bg-emerald-600'}`}
                    >
                        {finalPrice === 0 ? 'Baixar Grátis Agora' : `Gerar Pix de R$ ${finalPrice.toFixed(2).replace('.', ',')}`}
                    </button>
                </div>
            )}

            {state.step === 'loading' && (
                <div className="py-12 flex flex-col items-center justify-center text-center space-y-4">
                    <Loader2 size={48} className="text-emerald-500 animate-spin" />
                    <p className="text-slate-400">Gerando cobrança...</p>
                </div>
            )}

            {state.step === 'payment' && (
                <div className="space-y-6 text-center">
                    <p className="text-slate-300 text-sm">
                        Escaneie o QR Code abaixo ou copie o código Pix para pagar.
                        <br />
                        A liberação é automática após o pagamento.
                    </p>

                    {state.qrCodeImage && (
                        <div className="bg-white p-4 rounded-xl inline-block mx-auto">
                            <img src={`data:image/png;base64,${state.qrCodeImage}`} alt="Pix QR Code" className="w-48 h-48" />
                        </div>
                    )}

                    <div className="relative">
                        <input
                            readOnly
                            value={state.qrCodePayload || ''}
                            className="w-full bg-[#050505] border border-white/10 rounded-xl px-4 py-3 text-slate-400 text-xs pr-12 truncate font-mono"
                        />
                        <button
                            onClick={handleCopy}
                            className="absolute right-2 top-1/2 -translate-y-1/2 p-1.5 hover:bg-white/10 rounded-lg text-emerald-500 transition-colors"
                            title="Copiar"
                        >
                            {copied ? <Check size={16} /> : <Copy size={16} />}
                        </button>
                    </div>

                    <div className="flex items-center justify-center gap-2 text-xs text-slate-500 animate-pulse">
                        <Loader2 size={12} className="animate-spin" />
                        Aguardando pagamento...
                    </div>
                </div>
            )}

            {state.step === 'success' && (
                <div className="py-12 flex flex-col items-center justify-center text-center space-y-4">
                    <div className="w-16 h-16 bg-emerald-500 rounded-full flex items-center justify-center mb-2">
                        <Check size={32} className="text-black" />
                    </div>
                    <h3 className="text-2xl font-bold text-white">Pagamento Confirmado!</h3>
                    <p className="text-slate-400">Seu download iniciará em instantes.</p>
                </div>
            )}
        </Modal>
    );
}

'use client';

import React, { useMemo } from 'react';
import { ResumeData } from './ResumeGenerator';
import { CheckCircle2, AlertTriangle, XCircle, Info, ChevronDown, ChevronUp, BrainCircuit, Activity, FileText, Search, Fingerprint } from 'lucide-react';

interface ResumeAnalyzerProps {
    data: ResumeData;
    className?: string;
}

// Helper to count words
const countWords = (str: string) => {
    return str.trim().split(/\s+/).filter(w => w.length > 0).length;
};

// Helper to calculate score
const calculateScore = (checks: { passed: boolean; weight: number }[]) => {
    const totalWeight = checks.reduce((acc, c) => acc + c.weight, 0);
    const passedWeight = checks.reduce((acc, c) => acc + (c.passed ? c.weight : 0), 0);
    return Math.round((passedWeight / totalWeight) * 100);
};

export function ResumeAnalyzer({ data, className = "" }: ResumeAnalyzerProps) {
    // --- Analysis Logic ---
    const analysis = useMemo(() => {
        const checks = [];

        // 1. Synopsis (Structure & Length)
        const summaryWords = countWords(data.personal.summary || "");
        const experienceWords = data.experience.reduce((acc, exp) => acc + countWords(exp.description || ""), 0);
        const totalWords = summaryWords + experienceWords + countWords(data.personal.role || "") + data.skills.length; // Approximate

        // Heuristic for pages: ~400 words per page? 
        const estimatedPages = Math.ceil(totalWords / 400) || 1;

        const synopsis = {
            wordCount: {
                value: totalWords,
                status: totalWords >= 300 && totalWords <= 1200 ? 'success' : 'warning',
                msg: totalWords < 300 ? "Pouco conteúdo (min: 300)" : totalWords > 1200 ? "Muito conteúdo (max: 1200)" : "Tamanho ideal"
            },
            pageCount: {
                value: estimatedPages,
                status: estimatedPages <= 2 ? 'success' : 'warning',
                msg: estimatedPages > 2 ? "Mais de 2 páginas estimadas" : "Dentro do limite (1-2 pág)"
            },
            sections: {
                hasExperience: data.experience.length > 0,
                hasEducation: data.education.length > 0,
                hasSkills: data.skills.length > 0,
                hasProjects: data.projects.length > 0,
            }
        };

        // 2. Identification
        const id = {
            hasPhone: !!data.personal.phone,
            hasEmail: !!data.personal.email,
            hasLinkedin: !!data.personal.linkedin,
            hasLocation: !!data.personal.location,
        };

        // 3. Lexical (Pronouns, Formatting)
        const allText = (data.personal.summary + " " + data.experience.map(e => e.description).join(" ")).toLowerCase();

        // Check for personal pronouns (Eu, mim, meu)
        const pronounRegex = /\b(eu|mim|meu|minha|meus|minhas)\b/gi;
        const pronounsFound = (allText.match(pronounRegex) || []).length;

        // Check for numbers written as words (um, dois, três... dez)
        const numWordsRegex = /\b(um|dois|três|quatro|cinco|seis|sete|oito|nove|dez)\b/gi;
        const numWordsFound = (allText.match(numWordsRegex) || []).length;

        // Avg word length (simple vocabulary proxy)
        const words = allText.split(/\s+/).filter(w => w.length > 0);
        const avgWordLen = words.length > 0 ? words.reduce((a, b) => a + b.length, 0) / words.length : 0;
        const vocabLevel = avgWordLen > 5 ? 'Alto' : avgWordLen > 4 ? 'Médio' : 'Baixo';

        // 4. Semantic (Measurable, Skills)
        const measureTerms = ['%', 'R$', '$', 'aumentou', 'reduziu', 'gerou', 'melhorou', 'otimizou', 'liderou', 'crescimento'];
        // Simple check: does any experience description contain specific measurable patterns?
        // Look for digits followed by % or preceded by currency
        const measurableRegex = /(\d+%|R\$\s*\d|\$\d|aument(ou|ei)|reduz(iu|i))/i;
        const measurableCount = data.experience.filter(e => measurableRegex.test(e.description)).length;

        const hardSkillsCount = data.skills.length;
        // Basic check for soft skills keywords? (Hard to distinguish without a list)
        // Let's rely on hard skills count > 5 for "Good Technical Density"
        const efficiencyRatio = hardSkillsCount / (1 + (data.personal.summary.length / 100)); // Arbitrary logical check

        checks.push(
            { weight: 10, passed: synopsis.wordCount.status === 'success' },
            { weight: 10, passed: synopsis.pageCount.status === 'success' },
            { weight: 5, passed: synopsis.sections.hasExperience },
            { weight: 5, passed: synopsis.sections.hasEducation },
            { weight: 5, passed: synopsis.sections.hasSkills },
            { weight: 10, passed: id.hasEmail && id.hasPhone },
            { weight: 5, passed: id.hasLinkedin },
            { weight: 10, passed: pronounsFound === 0 },
            { weight: 5, passed: numWordsFound === 0 },
            { weight: 10, passed: measurableCount >= 1 } // At least one measurable achievement
        );

        const score = calculateScore(checks);

        return {
            synopsis,
            id,
            lexical: { pronounsFound, numWordsFound, vocabLevel },
            semantic: { measurableCount, hardSkillsCount },
            score
        };
    }, [data]);

    return (
        <div className={`space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500 ${className}`}>

            {/* Header / Score */}
            <div className="bg-[#0E0E0E] rounded-2xl p-8 border border-white/5 relative overflow-hidden flex flex-col md:flex-row items-center justify-between gap-8">
                {/* Background Blobs */}
                <div className="absolute top-0 right-0 w-64 h-64 bg-emerald-500/10 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2 pointer-events-none" />

                <div className="space-y-4 relative z-10 text-center md:text-left">
                    <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-purple-500/10 border border-purple-500/20 text-purple-400 text-xs font-bold uppercase tracking-widest">
                        <BrainCircuit size={14} /> Análise ATS
                    </div>
                    <h2 className="text-3xl font-bold text-white">Pontuação do Currículo</h2>
                    <p className="text-slate-400 max-w-lg">
                        Analisamos seu currículo com base nos principais critérios de sistemas de rastreamento (ATS) e recrutadores.
                    </p>
                </div>

                <div className="relative z-10 flex items-center justify-center">
                    <div className="relative w-32 h-32 flex items-center justify-center">
                        {/* Circular Progress (Simple SVG) */}
                        <svg className="w-full h-full -rotate-90" viewBox="0 0 36 36">
                            <path
                                d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                                fill="none"
                                stroke="#1e293b"
                                strokeWidth="3"
                            />
                            <path
                                d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                                fill="none"
                                stroke={analysis.score > 80 ? "#10b981" : analysis.score > 50 ? "#f59e0b" : "#ef4444"}
                                strokeWidth="3"
                                strokeDasharray={`${analysis.score}, 100`}
                                className="transition-all duration-1000 ease-out"
                            />
                        </svg>
                        <div className="absolute inset-0 flex flex-col items-center justify-center">
                            <span className="text-3xl font-black text-white">{analysis.score}</span>
                            <span className="text-[10px] uppercase font-bold text-slate-500">de 100</span>
                        </div>
                    </div>
                </div>
            </div>

            {/* Grid of Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

                {/* 1. Synopsis */}
                <Card
                    title="Sinopse do Documento"
                    icon={<FileText className="text-blue-400" size={20} />}
                    scoreColor="blue"
                >
                    <CheckItem
                        passed={analysis.synopsis.wordCount.status === 'success'}
                        label="Contagem de Palavras"
                        value={`${analysis.synopsis.wordCount.value}`}
                        sub={analysis.synopsis.wordCount.msg}
                    />
                    <CheckItem
                        passed={analysis.synopsis.pageCount.status === 'success'}
                        label="Páginas Estimadas"
                        value={`~${analysis.synopsis.pageCount.value}`}
                        sub={analysis.synopsis.pageCount.msg}
                    />
                    <div className="pt-2 border-t border-white/5 space-y-2">
                        <LabelStatus passed={analysis.synopsis.sections.hasExperience} label="Seção de Experiência" />
                        <LabelStatus passed={analysis.synopsis.sections.hasEducation} label="Seção de Educação" />
                        <LabelStatus passed={analysis.synopsis.sections.hasSkills} label="Seção de Habilidades" />
                        <LabelStatus passed={analysis.synopsis.sections.hasProjects} label="Seção de Projetos" />
                    </div>
                </Card>

                {/* 2. Identification */}
                <Card
                    title="Identificação de Dados"
                    icon={<Fingerprint className="text-emerald-400" size={20} />}
                    scoreColor="emerald"
                >
                    <LabelStatus passed={analysis.id.hasEmail} label="Email Profissional" />
                    <LabelStatus passed={analysis.id.hasPhone} label="Telefone de Contato" />
                    <LabelStatus passed={analysis.id.hasLinkedin} label="Link do LinkedIn" />
                    <LabelStatus passed={analysis.id.hasLocation} label="Localização" />
                    <div className="mt-4 p-3 bg-emerald-500/5 rounded-lg border border-emerald-500/10 text-xs text-slate-400">
                        <Info size={14} className="inline mr-1 text-emerald-500" />
                        Certifique-se de que seus links estão clicáveis e corretos.
                    </div>
                </Card>

                {/* 3. Lexical */}
                <Card
                    title="Análise Lexical"
                    icon={<Search className="text-purple-400" size={20} />}
                    scoreColor="purple"
                >
                    <CheckItem
                        passed={analysis.lexical.pronounsFound === 0}
                        label="Pronomes Pessoais (Eu/Mim)"
                        value={analysis.lexical.pronounsFound.toString()}
                        sub={analysis.lexical.pronounsFound > 0 ? "Evite usar 1ª pessoa" : "Ótimo, texto impessoal"}
                    />
                    <CheckItem
                        passed={analysis.lexical.numWordsFound === 0}
                        label="Números por Extenso"
                        value={analysis.lexical.numWordsFound.toString()}
                        sub={analysis.lexical.numWordsFound > 0 ? "Use dígitos (ex: 3) ao invés de texto" : "Correto"}
                    />
                    <CheckItem
                        passed={true}
                        label="Nível de Vocabulário"
                        value={analysis.lexical.vocabLevel}
                        sub="Baseado no tamanho médio das palavras"
                    />
                </Card>

                {/* 4. Semantic */}
                <Card
                    title="Análise Semântica"
                    icon={<Activity className="text-orange-400" size={20} />}
                    scoreColor="orange"
                >
                    <CheckItem
                        passed={analysis.semantic.measurableCount > 0}
                        label="Conquistas Mensuráveis"
                        value={`${analysis.semantic.measurableCount} itens`}
                        sub={analysis.semantic.measurableCount === 0 ? "Tente adicionar números e métricas (%, R$)" : "Bom uso de métricas"}
                    />
                    <CheckItem
                        passed={analysis.semantic.hardSkillsCount >= 5}
                        label="Densidade de Skills Técnicas"
                        value={`${analysis.semantic.hardSkillsCount}`}
                        sub={analysis.semantic.hardSkillsCount < 5 ? "Adicione mais tecnologias" : "Boa quantidade"}
                    />
                </Card>
            </div>

            {/* Hint */}
            <div className="flex items-start gap-4 p-4 rounded-xl bg-slate-900/50 border border-white/5 text-sm text-slate-400">
                <Info size={18} className="shrink-0 text-slate-500 mt-0.5" />
                <p>
                    Esta análise é uma simulação baseada em boas práticas de mercado. Pontuações acima de 80 são consideradas excelentes para passar em triagens automáticas.
                </p>
            </div>

        </div>
    );
}

// --- UI Components ---

function Card({ title, icon, children, scoreColor }: { title: string, icon: React.ReactNode, children: React.ReactNode, scoreColor: string }) {
    return (
        <div className="bg-[#0E0E0E] rounded-2xl p-6 border border-white/5 hover:border-white/10 transition-colors">
            <div className="flex items-center gap-3 mb-6">
                <div className={`p-2 rounded-lg bg-${scoreColor}-500/10 border border-${scoreColor}-500/20`}>
                    {icon}
                </div>
                <h3 className="font-bold text-white text-lg">{title}</h3>
            </div>
            <div className="space-y-4">
                {children}
            </div>
        </div>
    );
}

function CheckItem({ passed, label, value, sub }: { passed: boolean, label: string, value: string, sub?: string }) {
    return (
        <div className="flex justify-between items-start group">
            <div className="flex items-start gap-3">
                {passed ? (
                    <CheckCircle2 size={18} className="text-emerald-500 shrink-0 mt-0.5" />
                ) : (
                    <AlertTriangle size={18} className="text-amber-500 shrink-0 mt-0.5" />
                )}
                <div>
                    <p className={`text-sm font-medium ${passed ? "text-slate-300" : "text-amber-200"}`}>{label}</p>
                    {sub && <p className="text-[10px] text-slate-500 mt-0.5">{sub}</p>}
                </div>
            </div>
            <span className="text-xs font-bold text-slate-400 bg-white/5 px-2 py-1 rounded">{value}</span>
        </div>
    );
}

function LabelStatus({ passed, label }: { passed: boolean, label: string }) {
    return (
        <div className="flex items-center gap-2">
            {passed ? (
                <CheckCircle2 size={14} className="text-emerald-500" />
            ) : (
                <XCircle size={14} className="text-red-500" />
            )}
            <span className={`text-sm ${passed ? "text-slate-400" : "text-slate-500 line-through"}`}>{label}</span>
        </div>
    );
}

import React from 'react';
import { ResumeData } from './ResumeGenerator';
import { Plus, Trash } from 'lucide-react';
import { MonthYearPicker } from './MonthYearPicker';
import { Tooltip } from './Tooltip';

interface ResumeFormProps {
    data: ResumeData;
    onChange: (data: ResumeData) => void;
    step: number;
}

export function ResumeForm({ data, onChange, step }: ResumeFormProps) {
    const updatePersonal = (field: keyof ResumeData['personal'], value: string) => {
        onChange({
            ...data,
            personal: { ...data.personal, [field]: value }
        });
    };

    const addExperience = () => {
        onChange({
            ...data,
            experience: [
                ...data.experience,
                {
                    id: crypto.randomUUID(),
                    company: '',
                    role: '',
                    startDate: '',
                    endDate: '',
                    current: false,
                    description: ''
                }
            ]
        });
    };

    const updateExperience = (id: string, field: keyof ResumeData['experience'][0], value: any) => {
        onChange({
            ...data,
            experience: data.experience.map(exp => exp.id === id ? { ...exp, [field]: value } : exp)
        });
    };

    const removeExperience = (id: string) => {
        onChange({
            ...data,
            experience: data.experience.filter(exp => exp.id !== id)
        });
    };

    // Phone Mask Helper
    const formatPhone = (value: string) => {
        // Remove non-digits
        const digits = value.replace(/\D/g, '');
        // Limit to 11 digits
        const limited = digits.substring(0, 11);

        // Apply mask: (XX) XXXXX-XXXX or (XX) XXXX-XXXX
        if (limited.length > 2) {
            let formatted = `(${limited.substring(0, 2)}) `;
            if (limited.length > 7) {
                formatted += `${limited.substring(2, 7)}-${limited.substring(7)}`;
            } else {
                formatted += limited.substring(2);
            }
            return formatted;
        }
        return limited;
    };

    const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value;
        const formatted = formatPhone(value);
        updatePersonal('phone', formatted);
    };

    // Helper inputs styles
    const inputClass = "w-full bg-[#050505] border border-white/10 rounded-xl px-4 py-3.5 text-white focus:outline-none focus:border-emerald-500/50 focus:ring-1 focus:ring-emerald-500/50 transition-all placeholder:text-slate-700 shadow-inner";
    const labelClass = "block text-xs font-bold uppercase tracking-wider text-slate-400 mb-2.5 ml-1";

    return (
        <div className="space-y-8 animate-in fade-in slide-in-from-right-4 duration-500">


            {/* Personal Info */}
            {step === 0 && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="md:col-span-2 p-4 bg-emerald-500/5 border border-emerald-500/10 rounded-xl flex items-start gap-3 mb-2">
                        <div className="shrink-0 w-5 h-5 rounded-full bg-emerald-500/20 text-emerald-400 flex items-center justify-center mt-0.5">
                            <span className="block w-1.5 h-1.5 rounded-full bg-emerald-400" />
                        </div>
                        <p className="text-sm text-slate-300 leading-relaxed">
                            Preencha seus dados principais com atenção. O e-mail e LinkedIn são essenciais para recrutadores.
                        </p>
                    </div>

                    <div>
                        <label className={labelClass}>Nome Completo</label>
                        <input
                            type="text"
                            className={inputClass}
                            value={data.personal.fullName}
                            onChange={(e) => updatePersonal('fullName', e.target.value)}
                            placeholder="Ex: Ana Silva"
                            autoFocus
                        />
                    </div>
                    <div>
                        <label className={labelClass}>
                            Cargo / Título
                            <Tooltip content="O cargo que você ocupa ou almeja. Seja específico, ex: 'Senior Frontend Developer' ao invés de apenas 'Desenvolvedor'." />
                        </label>
                        <input
                            type="text"
                            className={inputClass}
                            value={data.personal.role}
                            onChange={(e) => updatePersonal('role', e.target.value)}
                            placeholder="Ex: Full Stack Developer"
                        />
                    </div>
                    <div>
                        <label className={labelClass}>Email</label>
                        <input
                            type="email"
                            className={inputClass}
                            value={data.personal.email}
                            onChange={(e) => updatePersonal('email', e.target.value)}
                            placeholder="email@exemplo.com"
                        />
                    </div>
                    <div>
                        <label className={labelClass}>Telefone</label>
                        <input
                            type="text"
                            className={inputClass}
                            value={data.personal.phone}
                            onChange={handlePhoneChange}
                            maxLength={15}
                            placeholder="(11) 99999-9999"
                        />
                    </div>
                    <div>
                        <label className={labelClass}>LinkedIn</label>
                        <input
                            type="text"
                            className={inputClass}
                            value={data.personal.linkedin}
                            onChange={(e) => updatePersonal('linkedin', e.target.value)}
                            placeholder="linkedin.com/in/..."
                        />
                    </div>
                    <div>
                        <label className={labelClass}>Localização</label>
                        <input
                            type="text"
                            className={inputClass}
                            value={data.personal.location}
                            onChange={(e) => updatePersonal('location', e.target.value)}
                            placeholder="São Paulo, SP"
                        />
                    </div>
                    <div className="md:col-span-2">
                        <label className={labelClass}>
                            Resumo Profissional
                            <Tooltip content="Um parágrafo curto (3-4 linhas) destacando seus anos de experiência, principais stacks e maiores conquistas. Use palavras-chave da sua área." />
                        </label>
                        <textarea
                            className={`${inputClass} min-h-[140px] resize-y`}
                            value={data.personal.summary}
                            onChange={(e) => updatePersonal('summary', e.target.value)}
                            placeholder="Breve resumo sobre suas qualificações, anos de experiência e principais objetivos profissionais..."
                        />
                    </div>
                </div>
            )}

            {/* Experience */}
            {step === 1 && (
                <div>
                    <div className="flex justify-end mb-6">
                        <button
                            onClick={addExperience}
                            className="flex items-center gap-2 px-4 py-2 bg-emerald-500/10 text-emerald-400 rounded-lg text-sm font-bold uppercase hover:bg-emerald-500/20 transition-all border border-emerald-500/20 hover:border-emerald-500/40"
                        >
                            <Plus size={16} /> Adicionar Experiência
                        </button>
                    </div>

                    <div className="space-y-6">
                        {data.experience.map((exp, index) => (
                            <div key={exp.id} className="bg-[#0A0A0A] rounded-2xl p-6 md:p-8 border border-white/5 relative group animate-in slide-in-from-bottom-4 fade-in duration-500 fill-mode-both" style={{ animationDelay: `${index * 100}ms` }}>
                                <div className="absolute -left-[1px] top-8 bottom-8 w-[2px] bg-gradient-to-b from-emerald-500/0 via-emerald-500/50 to-emerald-500/0 hidden md:block opacity-0 group-hover:opacity-100 transition-opacity" />

                                <button
                                    onClick={() => removeExperience(exp.id)}
                                    className="absolute top-4 right-4 p-2 text-slate-600 hover:text-red-400 hover:bg-red-400/10 rounded-lg transition-all"
                                    title="Remover Experiência"
                                >
                                    <Trash size={18} />
                                </button>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
                                    <div className="md:col-span-2">
                                        <label className={labelClass}>Nome da Empresa</label>
                                        <input
                                            type="text"
                                            className={inputClass}
                                            value={exp.company}
                                            onChange={(e) => updateExperience(exp.id, 'company', e.target.value)}
                                            autoFocus={index === data.experience.length - 1} // Autofocus on newly added item
                                            placeholder="Ex: Google, Microsoft, Startup X"
                                        />
                                    </div>
                                    <div>
                                        <label className={labelClass}>Cargo</label>
                                        <input
                                            type="text"
                                            className={inputClass}
                                            value={exp.role}
                                            onChange={(e) => updateExperience(exp.id, 'role', e.target.value)}
                                            placeholder="Ex: Senior Frontend Engineer"
                                        />
                                    </div>
                                    <div className="flex gap-4">
                                        <div className="flex-1">
                                            <MonthYearPicker
                                                label="Início"
                                                value={exp.startDate}
                                                onChange={(val) => updateExperience(exp.id, 'startDate', val)}
                                            />
                                        </div>
                                        <div className="flex-1">
                                            <MonthYearPicker
                                                label="Fim"
                                                value={exp.endDate}
                                                onChange={(val) => updateExperience(exp.id, 'endDate', val)}
                                                disabled={exp.current}
                                            />
                                        </div>
                                    </div>
                                    <div className="flex gap-4">
                                        <div className="flex-1">
                                            <label className={labelClass}>Modalidade</label>
                                            <select
                                                className={inputClass}
                                                value={exp.workMode || ''}
                                                onChange={(e) => updateExperience(exp.id, 'workMode', e.target.value)}
                                            >
                                                <option value="">Selecione...</option>
                                                <option value="Presencial">Presencial</option>
                                                <option value="Remoto">Remoto</option>
                                                <option value="Híbrido">Híbrido</option>
                                            </select>
                                        </div>
                                        <div className="flex-1 flex items-end">
                                            <label className="inline-flex items-center gap-3 px-3 py-2 rounded-lg bg-white/5 hover:bg-white/10 cursor-pointer transition-colors group/check w-full h-[50px]"> {/* matched height */}
                                                <div className={`w-5 h-5 rounded border flex items-center justify-center transition-colors ${exp.current ? 'bg-emerald-500 border-emerald-500' : 'border-slate-600 bg-transparent'}`}>
                                                    {exp.current && <span className="block w-2 h-2 bg-white rounded-full" />}
                                                </div>
                                                <input
                                                    type="checkbox"
                                                    checked={exp.current}
